# 𝑪𝒐𝒑𝒚𝒓𝒊𝒈𝒉𝒕 𝑩𝒚 𝑨𝒍𝒕𝒓𝒐𝒏
# 𝑨𝒍𝒍 𝑹𝒊𝒈𝒉𝒕𝒔 𝑹𝒆𝒔𝒆𝒓𝒗𝒆𝒅

import asyncio
from random import choice
from pyrogram.types import Message
from pyrogram import filters, Client
from config import SUDO_USERS
from data import GROUP, PORM


__NAME__ = "Sᴘᴀᴍ"
__HELP__ = """
⊱ `spam` : sᴘᴀᴍ ᴍᴇssᴀɢᴇs 

⊱ `pspam` : sᴘᴀᴍ ᴘᴏʀɴ ᴠɪᴅᴇᴏ ᴀɴᴅ ᴘʜᴏᴛᴏs

⊱ `hang` : ʜᴀɴɢ ᴀɴʏ ɢʀᴏᴜᴘ ᴏʀ ɪsᴇʀ's ɪɴʙᴏx
"""

GC = 1

@Client.on_message(filters.command(["spam"], [".", "!", "/"]) & filters.user(SUDO_USERS), group=GC)
async def altspam(xspam: Client, message: Message):
    alt = message.text.split(" ", 2)
    if  len(alt) == 3:
        quantity, spam_text = int(alt[1]), alt[2]
        if message.reply_to_message:
            id = message.reply_to_message_id
            for _ in range(quantity):
                await message.reply_text(spam_text, reply_to_message_id=id)
                await asyncio.sleep(0.3)
        else:
            cid = message.chat.id
            for _ in range(quantity):
                await xspam.send_message(cid, spam_text)
                await asyncio.sleep(0.3)

    elif message.reply_to_message:
        spam_text = message.reply_to_message.text
        quantity = int(alt[1])
        cid = message.chat.id
        for _ in range(quantity):
            await xspam.send_message(cid, spam_text)
            await asyncio.sleep(0.3)

    else:
        await message.reply_text(f"😈 **ᴜsᴀɢᴇ:**\n\n» !spam 13 Altron\n» !spam 13 <ʀᴇᴘʟʏ ᴛᴏ ᴛᴇxᴛ>\n\n**To do spam with replying to a user:**\n» !spam 13 Altron <ʀᴇᴘʟʏ ᴛᴏ ᴜꜱᴇʀ>")


@Client.on_message(filters.command(["pspam", "pornspam"], [".", "/", "!"]) & filters.user(SUDO_USERS), group=GC)
async def pspam(xspam: Client, message: Message):
    cid = message.chat.id
    if int(cid) in GROUP:
        await message.reply_text("» ꜱᴏʀʀʏ, ᴛʜɪꜱ ɪꜱ ᴘʀᴏᴛᴇᴄᴛᴇᴅ ɢʀᴏᴜᴘ !")
        return

    altp = message.text.split(" ", 2)
    if len(altp) > 1:
        quantity = int(altp[1])
        for _ in range(quantity):
            porm = choice(PORM)
            await xspam.send_video(cid, porm)
            await asyncio.sleep(0.3)
    else:
        await message.reply_text(f"🔞 **ᴜsᴀɢᴇ:**  !pspam 13")


@Client.on_message(filters.command(["hang"], ["/", "!", "."]) & filters.user(SUDO_USERS), group=GC)
async def hang(xspam: Client, message: Message): 
    alt = message.text.split(" ")
    if len(alt) == 1:
        await message.reply_text(f"**Usage:** !hang 10")
        return
    if int(message.chat.id) in GROUP:
        await message.reply_text("__sᴏʀʀʏ ɪ ᴄᴀɴ'ᴛ ʜᴀɴɢ ʜᴇʀᴇ__")
